import React from 'react'

const nav_links =[
	{
		path: '#home',
		display: 'Trang chủ'
	},

	{
		path: '#news',
		display: 'Tin tức'
	},

	{
		path: '#contact',
		display: 'Liên hệ'
	},

	{
		path: '#cart',
		display: 'Giỏ hàng'
	},

	{
		path: '#service',
		display: 'Dịch vụ'
	},

	{
		path: '#combo',
		display: 'Combo'
	},

	{
		path: '#material',
		display: 'Nguyên liệu'
	},

	{
		path: '#partner',
		display: 'Đối tác'
	},

	{
		path: '#formula',
		display: 'Công thức nấu ăn'
	},
]

const Header = () => {
	return (
		<header className="header">
			<div className="container">
				<div className="nav_wrapper">
					<div className="logo">
						<h2>Cỗ</h2>
						<p>Grow with us</p>
					</div>

					{/*  ========= navigation ===========	*/}

					<div className="navigation">
						<ul className="menu">
							{
								nav_links.map((item,index)=>(
									<li className="menu_item"><a href=
									{item.path} className="menu_link">{item.display}
									</a>
								</li>
								))
							}
						</ul>
					</div>

					{/*  ====================== light mode ================	*/}

					<div className="light_mode">
						<span><i class="ri-sun-line"></i> Light Mode</span>
					</div>
				</div>
			</div>
		</header>
		)
}

export default Header
